INSERT INTO courses VALUES ("ANATOMY 200R","Research in Anatomy and Neurobiology for First-Year Students","2-12","Independent research within the laboratories of graduate training faculty in the Department of Anatomy and Neurobiology for first-year Ph.D. students.","","");
INSERT INTO courses VALUES ("ANATOMY 201","Human Gross Anatomy","8","Study and dissection of the human body, including muscular, skeletal, nervous, and cardiovascular systems. Emphasis on both normal and abnormal structure and function.",""," Graduate students only.");
INSERT INTO courses VALUES ("ANATOMY 202B","Human Neuroscience","4","Study of the human nervous system at the systems level including the physiology and anatomy of sensory, motor, and integrative functions."," ANATOMY 202A","");
INSERT INTO courses VALUES ("ANATOMY 203A","Human Microscopoic Anatomy","3","Lecture and laboratory course on human microscopic anatomy. Emphasis is on functional implications of structure of cells and tissues.",""," Graduate students only.");
INSERT INTO courses VALUES ("ANATOMY 203B","Human Microscopoic Anatomy","3","Lecture and laboratory course on human microscopic anatomy. Emphasis is on functional implications of structure of cells and tissues."," ANATOMY 203A"," Graduate students only.");
INSERT INTO courses VALUES ("ANATOMY 206","Tutorial in Anatomy","3","Series of tutorials on advanced topics in anatomy.","","");
INSERT INTO courses VALUES ("ANATOMY 210A","Systems Neuroscience","5","Study of the mammalian nervous system at the systems level. Anatomy and physiology of sensory, motor, and integrative functions.",""," Graduate students only. Neurobiology and Behavior Majors only.");
INSERT INTO courses VALUES ("ANATOMY 215","Epilepsy as a Window to Mechanisms of Neuronal Plasticity","4","Understanding the mechanisms of brain disorders provides novel insights into the normal function of neurons and circuits. Discusses approaches to studying mechanisms of brain function ranging from imaging, the use of models and others to study epilepsy.","","");
INSERT INTO courses VALUES ("ANATOMY 227A","Current Topics in Neuroscience","1-4","Focuses on critical reading, presentation, and discussion of current literature in neuroscience research.",""," Graduate students only.");
INSERT INTO courses VALUES ("ANATOMY 227B","Current Topics in Neuroscience","1-4","Focuses on critical reading, presentation, and discussion of current literature in neuroscience research.",""," Graduate students only.");
INSERT INTO courses VALUES ("ANATOMY 227C","Current Topics in Neuroscience","1-4","Focuses on critical reading, presentation, and discussion of current literature in neuroscience research.",""," Graduate students only.");
INSERT INTO courses VALUES ("ANATOMY 230","Topics in Translational Neuroscience","2-4","One-hour seminar presentation by participating faculty or guest lecturer and open to the science community, followed by one-hour student discussion of the lecture topic or related topic. Students are responsible for presentations and readings.",""," Graduate students only. Interdepartmental Neuroscience Majors have first consideration for enrollment.");
INSERT INTO courses VALUES ("ANATOMY 230A","Molecular, Cellular, & Developmental Neurobiology","4","Molecular aspects of the structure and function of neurons and glia including neurotransmission, synaptic modulation, and channels.  Neural development at the cellular and molecular level including neurogenesis, pattern formation, trophic factors, axonal growth, and synaptic rearrangement.",""," Graduate students only.");
INSERT INTO courses VALUES ("ANATOMY 292A","Scientific Communication","2","Small group meetings for graduate students to practice scientific writing, debate, and presentation skills.","","");
INSERT INTO courses VALUES ("ANATOMY 292B","Scientific Communication","2","Small group meetings for graduate students to practice scientific writing, debate, and presentation skills.","","");
INSERT INTO courses VALUES ("ANATOMY 292C","Scientific Communication","2","Small group meetings for graduate students to practice scientific writing, debate, and presentation skills.","","");
INSERT INTO courses VALUES ("ANTHRO 2A","Introduction to Sociocultural Anthropology","4","Introduction to cultural diversity and the methods used by anthropologists to account for it. Family relations, economic activities, politics, gender, and religion in a wide range of societies. Stresses the application of anthropological methods to research problems.","","");
INSERT INTO courses_in_ge VALUES ("ANTHRO 2A","(III and VIII )");
INSERT INTO courses VALUES ("ANTHRO 2B","Introduction to Biological Anthropology","4","Evolutionary theory and processes, comparative primate fossil record, human variation, and the adequacy of theory, and empirical data.","","");
INSERT INTO courses_in_ge VALUES ("ANTHRO 2B","(III)");
INSERT INTO courses VALUES ("ANTHRO 2C","Introduction to Archaeology","4","Archaeological theory and cultural processes with emphasis on the American Southwest, Mesoamerica, and Mesopotamia.","","");
INSERT INTO courses_in_ge VALUES ("ANTHRO 2C","(III)");
INSERT INTO courses VALUES ("ANTHRO 2D","Introduction to Language and Culture","4","Explores what the study of language can reveal about ourselves as bearers of culture. After introducing some basic concepts, examines how cultural knowledge is linguistically organized and how language might shape our perception of the world.","","");
INSERT INTO courses_in_ge VALUES ("ANTHRO 2D","(III)");
INSERT INTO courses VALUES ("ANTHRO 10A","Probability and Statistics","4","An introduction to probability and statistics. Emphasis on a thorough understanding of the probabilistic basis of statistical inference. Emphasizes examples from sociology, anthropology, and related social science disciplines.",""," Anthropology Majors have first consideration for enrollment. Sociology Majors have first consideration for enrollment.");
