INSERT INTO courses VALUES ("EECS 270AP","Advanced Analog Integrated Circuit Design I","4","Basic transistor configurations"," Recommended: Introductory knowledge of control systems (e.g. EECS 160A) and electronics (e.g. EECS 170C)."," Master of Engineering students only. Graduate students only.");
INSERT INTO courses VALUES ("EECS 270B","Advanced Analog Integrated Circuit Design II","4","Advanced transistor modeling issues"," EECS 270A"," Graduate students only.");
