INSERT INTO courses VALUES ("LPS 29","Critical Reasoning","4","Introduction to analysis and reasoning. The concepts of argument, premise, and conclusion, validity and invalidity, consistency and inconsistency. Identifying and assessing premises and inferences. Deductive versus inductive reasoning, and introduction to the probability calculus. Evaluating definitions. Informal fallacies.","","");
INSERT INTO courses_in_ge VALUES ("LPS 29","(II and Vb )");
INSERT INTO courses VALUES ("LPS 30","Introduction to Symbolic Logic","4","An introduction to the symbolism and methods of the logic of statements, including evaluation of arguments by truth tables, the techniques of natural deduction, and semantic tableaux.","","");
INSERT INTO courses_in_ge VALUES ("LPS 30","(Vb)");
INSERT INTO courses VALUES ("LPS 31","Introduction to Inductive Logic","4","Philosophical questions concerning the foundations of scientific inference, e.g., the traditional problem of induction, the Goodman paradox, the concept of cause, Mill's method of inductive reasoning, probability calculus, different interpretations of probability, and their interaction in inductive reasoning.","","");
INSERT INTO courses_in_ge VALUES ("LPS 31","(II, Va)");
INSERT INTO courses VALUES ("LPS 40","The Nature of Scientific Inquiry","4","Investigates the nature, scope, and status of scientific knowledge and the methods used to acquire it. Uses concrete historical examples from a variety of scientific fields to identify distinctive features of the scientific enterprise and explore their significance.","","");
INSERT INTO courses_in_ge VALUES ("LPS 40","(II)");
INSERT INTO courses VALUES ("LPS 60","The Making of Modern Science","4","Surveys the history of science and mathematics since the Scientific Revolution, examining central developments both chronologically and thematically, as well as investigating their significance for contemporary philosophical debates about the role and status of current scientific theories.","","");
INSERT INTO courses_in_ge VALUES ("LPS 60","(GE II or GE IV )");
INSERT INTO courses VALUES ("LPS H80","Scientific Realism and Instrumentalism","4","Explores competing views of the character and status of theoretical knowledge in science, including challenges to and defenses of the view that contemporary scientific theories offer straightforward and accurate descriptions of how things stand in otherwise inaccessible domains of nature.",""," Campuswide Honors Collegium students only.");
INSERT INTO courses_in_ge VALUES ("LPS H80","(II)");
INSERT INTO courses VALUES ("LPS H81","What is Space?","4","Historical, philosophical, scientific exploration of the concept of 'space.' Questions of interest include: What kind of a thing is space? How can we know what space is like? How is space different from time.",""," Campuswide Honors Collegium students only.");
INSERT INTO courses_in_ge VALUES ("LPS H81","(II)");
INSERT INTO courses VALUES ("LPS 91","The Philosophy of Sex","4","Discusses the origins of biological sex, dynamics of sexual selection, sex differences in humans, and the construction of gender in human societies. Seeks to understand the role social values play in the creation of science.","","");
INSERT INTO courses_in_ge VALUES ("LPS 91","(III)");
INSERT INTO courses VALUES ("LPS H91","The Philosophy and Biology of Sex","4","Covers the origins of biological sex, dynamics of sexual selection, the evolution and cultural creation of sexual behavior in humans, and the construction of gender in human societies.",""," Campuswide Honors Collegium students only.");
INSERT INTO courses_in_ge VALUES ("LPS H91","(II and III )");
INSERT INTO courses VALUES ("LPS H95","Jurisprudence and Constitutional Law","4","Applies competing theories of the nature of law and legal reasoning to evaluate decisions of the U.S. Supreme Court in controversial areas of constitutional law such as free speech, privacy, sexual conduct, affirmative action, and political campaign contributions.",""," Campuswide Honors Collegium students only.");
INSERT INTO courses_in_ge VALUES ("LPS H95","(III)");
