INSERT INTO courses VALUES ("LSCI 202A","Skills for Language Science I","4","Focuses on computational skills for language science research. Topics include computational literacy, programming languages, and database interfaces that are useful for language science research, allowing for general scripting and file manipulation, and enabling statistical analysis and data visualization.",""," Graduate students only.");
