INSERT INTO courses VALUES ("MOL BIO 200B","Research in Molecular Biology and Biochemistry","2-12","Individual research with Molecular Biology and Biochemistry faculty.",""," Graduate students only.");
INSERT INTO courses VALUES ("MOL BIO 200C","Research in Molecular Biology and Biochemistry","2-12","Individual research with Molecular Biology and Biochemistry faculty.",""," Graduate students only.");
