INSERT INTO courses VALUES ("MOL BIO 201A","Seminars in Molecular Biology & Biochemistry","2","Presentation of research from department laboratories or, when pertinent, of other recent developments.","","");
INSERT INTO courses VALUES ("MOL BIO 201B","Seminars in Molecular Biology & Biochemistry","2","Presentation of research from department laboratories or, when pertinent, of other recent developments.","","");
