INSERT INTO courses_in_ge VALUES ("MUSIC 40D","(IV and VIII )");
INSERT INTO courses VALUES ("MUSIC 41","Major Composer","4","Study of the works of an important composer with emphasis on their significance in historical and social contexts. Composers selected represent a wide variety of historical periods, nationalities, and stylistic orientations.","","");
INSERT INTO courses_in_ge VALUES ("MUSIC 41","(IV)");
INSERT INTO courses VALUES ("MUSIC 42","Music and Gender","4","Explores various intersections between music and gender. Examines ways in which gender and sexuality have played a vital role in the creation, dissemination, and consumption of music ranging from medieval Christian mysticism to contemporary hip-hop artists.","","");
INSERT INTO courses_in_ge VALUES ("MUSIC 42","(IV, VIII)");
INSERT INTO courses VALUES ("MUSIC 44","Classical Music in Society","4","Examines some ways in which classical music has played an integral role in aspects of society. Studies pieces from different cultural, political, and historical contexts in order to understand how musical meaning is created and interpreted.","","");
