INSERT INTO courses VALUES ("PERSIAN 10B","Persian Peer-to-Peer Language Mentor Program","1","Peer-to-Peer Language Mentor Program in which student mentors work with student mentees to increase the language skills of both participants. One hour weekly meetings. PERSIAN 10B is for language mentees.","","");
