INSERT INTO courses VALUES ("PHYSICS 52A","Fundamentals of Experimental Physics","2","Optics: lenses, mirrors, polarization, lasers, optical fibers, interference, spectra. Materials fee.",""," Physics Majors have first consideration for enrollment.");
INSERT INTO courses VALUES ("PHYSICS 52B","Fundamentals of Experimental Physics","2","Circuits: oscilloscope, meters, DC and AC circuits. Materials fee."," PHYSICS 7D or PHYSICS 3B"," Physics Majors have first consideration for enrollment.");
