INSERT INTO courses VALUES ("PHYSICS H90","The Idiom and Practice of Science","4","A series of fundamental and applied scientific problems of social relevance. Possible topics include Newton's Law, calculus, earthquake physics, and radiation.",""," Campuswide Honors Collegium students only.");
INSERT INTO courses_in_ge VALUES ("PHYSICS H90","(II, Va)");
INSERT INTO courses VALUES ("PHYSICS 99","Current Topics in Physics","2","Designed to introduce undergraduate students to current topics in physics. Focus is discussion of selected readings on current research issues.","","");
INSERT INTO courses VALUES ("PHYSICS 100","Computational Methods","4","Mathematical and numerical analysis using Mathematica and C programming, as applied to problems in physical science.","Corequisite: MATH 2E MATH 3A or MATH H3A","");
