import 'bootstrap/dist/css/bootstrap.min.css';
import { useDrop } from 'react-dnd';
import CourseCard from './CourseCard';
import ItemTypes from '../assets/ItemTypes';

function QuarterColumn({courses, quarter, onDrop}) {

  const [{ isOver }, dropRef] = useDrop(() => ({
    accept: ItemTypes,
    drop: (item, monitor) => onDrop(item.item, quarter),
    collect: (monitor) => ({
      isOver: monitor.isOver(),
    }),
  })); 

  return (
    <div 
    ref={dropRef}
    style={{height:'200px',backgroundColor: isOver?"#90CAF9":"#while",}}> 
      {courses.map((course) => 
        <CourseCard 
        key={course.id}
        item={course}
        index={course.id}
        > 
        </CourseCard> )}
    </div>
  );
}

export default QuarterColumn;
