import 'bootstrap/dist/css/bootstrap.min.css';
import required_ics from '../assets/icsrequirements';
import {Container, Row, Col} from 'react-bootstrap';
import {icsData, data, data1} from '../assets/IcsData'
import CourseCard from './CourseCard'

const Requirements = () => {

    let courseArray = Object.entries(required_ics)
    let index = 0

    function renderCol(){  
        let columns = []
        for(index; index < courseArray.length && courseArray[index][1]; index++){
            console.log(courseArray[index][0])
            if(icsData[courseArray[index][0]] !== null)
                columns.push(<Col key = {courseArray[index][0]}>{icsData[courseArray[index][0]]}</Col>)
            else
                columns.push(<Col key = {courseArray[index][0]}>{courseArray[index][0]}</Col>)
        }
        return columns
    }

    function renderRequirements(){        
        var rows = []
        console.log(icsData)
        for(index; index < courseArray.length; index++){
            if(!courseArray[index][1]){
                rows.push(<Row key = {courseArray[index][0]}>{courseArray[index][0]}
                {renderCol(index)}</Row>)
            }

        }
        return rows
    }

    return (
        <Container>
            {renderRequirements()}
        </Container>
    )
}

export default Requirements
