import 'bootstrap/dist/css/bootstrap.min.css';
import required_ics from '../assets/icsrequirements';
import {Container, Row, Col} from 'react-bootstrap';
import ics_data from '../assets/data'
import CourseCard from './CourseCard'

const Requirements = () => {

    let courseArray = Object.entries(required_ics)
    let index = 0

    function renderCol()
    {  
        let columns = []

        for(index; index < courseArray.length && courseArray[index][1]; index++)
        {
            if(data[courseArray[index][0]] !== null)
                columns.push(<Col key = {courseArray[index][0]}>{data[courseArray[index][0]]}</Col>)
            else
                columns.push(<Col key = {courseArray[index][0]}>{courseArray[index][0]}</Col>)
        }
        return columns
    }

    function renderRequirements()
    {        
        var rows = []
        //(required_ics)
        for(index; index < courseArray.length; index++)
        {
            console.log(courseArray[index][1])
            if(!courseArray[index][1])
            {
                rows.push(<Row key = {courseArray[index][0]}>{courseArray[index][0]}
                {renderCol(index)}</Row>)

            }

        }
        return rows
    }

    return (
        <Container>
            {renderRequirements()}
        </Container>
    )
}

export default Requirements
